import { useState } from "react";

function FaceMalfunctioningGraph({ days }) {
  const [selectedMalfunctioningDay, setSelectedMalfunctioningSay] =
    useState(Object.keys(days).length > 0
        ? Object.entries(days)[0][0]
        : null);

  const options = useMemo(() => {
    return {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        legend: {
          position: "top",
        },
        title: {
          display: true,
          text: selectedDay ? selectedDay : "Chart",
        },
        scales: {
          y: {
            beginAtZero: true,
            title: {
              display: true,
              text: "Temperatures",
            },
          },
          x: {
            title: {
              display: true,
              text: "Hour",
            },
          },
        },
        tooltip: {
          enabled: true,
          mode: "nearest",
          intersect: false,
          callbacks: {
            label: function (context) {
              return `${context.raw}˚`;
            },
          },
        },
      },
    };
  }, [selectedMalfunctioningDay]);
  return (
    <>
      <div className="flex flex-row flex-wrap w-[50vw] gap-x-[10px] gap-y-[10px] border-solid border-[1px] p-4">
        {Object.keys(days).map((day, dayIndex) => {
          return (
            <button
              className="bg-blue-10 text-white-10 p-2 rounded-md"
              key={dayIndex}
              onClick={() => setSelectedMalfunctioningSay(day)}
            >
              {day}
            </button>
          );
        })}
      </div>
    </>
  );
}

export default FaceMalfunctioningGraph;
