import { useCallback, useEffect, useMemo, useState } from "react";
import WeeklyReporting from "./components/WeeklyReporting";



function App() {


  const [weeklyMalfunctioningReport, setWeeklyMalfunctioningReport] = useState(null);
  const [selectedMalfunctioningDay, setSelectedMalfunctioningSay] = useState(null);



  const getWeeklyMalfunctioningReport = useCallback(async () => {
    const response = await fetch("/api/get_weekly_malfunctioning_summary_report", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await response.json();
    console.log(data);

    const weeklyMalfunctioningReportData = data.weeklyMalfunctioningReport || {};
    setWeeklyMalfunctioningReport(weeklyMalfunctioningReportData);
    setSelectedMalfunctioningSay(
      Object.keys(weeklyMalfunctioningReportData).length > 0
        ? Object.entries(weeklyMalfunctioningReportData)[0][0]
        : null
    );
  },[]);



  useEffect(() => {
    if (!weeklyReport) {
      getWeeklyReport();
    }

    if(!weeklyMalfunctioningReport) {
      getWeeklyMalfunctioningReport();
    }    
  }, [weeklyReport, weeklyMalfunctioningReport, getWeeklyReport, getWeeklyMalfunctioningReport]);

  return (
    <div className="bg-white-20 h-[100dvh] p-10 flex flex-col items-center">
      <h1 className="text-[25px] m-10">Reporting</h1>
      <WeeklyReporting/>
    </div>
  );
}

export default App;
