import { useState } from "react";
import Reporting from "./components/Reporting";

function App() {
  const [selectedView, setSelectedView] = useState("report");

  return (
    <div className="bg-white-20 h-[100dvh] p-10 flex flex-col gap-y-10 items-center overflow-hidden overflow-y-auto ">
      <div className="flex flex-row w-full gap-10 border-b-[1px] border-gray-0 border-solid">
        <button onClick={() => setSelectedView("report")}>Reporting</button>
        <button onClick={() => setSelectedView("control")}>Control Panel</button>
      </div>
      {selectedView === "report" && <Reporting/>}
      {selectedView === "control" && <Reporting/>}      
    </div>
  );
}

export default App;
