import { useCallback, useEffect, useMemo, useState } from "react";
import WeeklyReporting from "./components/WeeklyReporting";

function App() {
  const [weeklyMalfunctioningReport, setWeeklyMalfunctioningReport] =
    useState(null);
  const [selectedMalfunctioningDay, setSelectedMalfunctioningSay] =
    useState(null);

  const getWeeklyMalfunctioningReport = useCallback(async () => {
    const response = await fetch(
      "/api/get_weekly_malfunctioning_summary_report",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    const data = await response.json();
    console.log(data);

    const weeklyMalfunctioningReportData =
      data.weeklyMalfunctioningReport || {};
    setWeeklyMalfunctioningReport(weeklyMalfunctioningReportData);
    setSelectedMalfunctioningSay(
      Object.keys(weeklyMalfunctioningReportData).length > 0
        ? Object.entries(weeklyMalfunctioningReportData)[0][0]
        : null
    );
  }, []);

  useEffect(() => {
    if (!weeklyMalfunctioningReport) {
      getWeeklyMalfunctioningReport();
    }
  }, [weeklyMalfunctioningReport, getWeeklyMalfunctioningReport]);

  return (
    <div className="bg-white-20 h-[100dvh] p-10 flex flex-col items-center">
      <h1 className="text-[25px] m-10">Reporting</h1>
      <WeeklyReporting />
      {
        !weeklyMalfunctioningReport || weeklyMalfunctioningReport.length === 0 ?
        <></>
        :
        <>
        {
          weeklyMalfunctioningReport.map((face, faceIndex) => {
            return <div className="flex flex-col items-center p-4 gap-y-4 border-solid border-[1px]">
              <div>{face.face}</div>
            </div>
          })
        }
        </>
      }
    </div>
  );
}

export default App;
