import { useMemo, useState } from "react";
import { Line } from "react-chartjs-2";

function FaceMalfunctioningGraph({ days }) {
  const [selectedMalfunctioningDay, setSelectedMalfunctioningSay] = useState(
    Object.keys(days).length > 0 ? Object.entries(days)[0][0] : null
  );

  const options = useMemo(() => {
    return {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        legend: {
          position: "top",
        },
        title: {
          display: true,
          text: selectedMalfunctioningDay ? selectedMalfunctioningDay : "Chart",
        },
        scales: {
          y: {
            beginAtZero: true,
            title: {
              display: true,
              text: "Temperatures",
            },
          },
          x: {
            title: {
              display: true,
              text: "Hour",
            },
          },
        },
        tooltip: {
          enabled: true,
          mode: "nearest",
          intersect: false,
          callbacks: {
            label: function (context) {
              return `${context.raw}˚`;
            },
          },
        },
      },
    };
  }, [selectedMalfunctioningDay]);

  const labels = useMemo(() => {
    return Object.keys(days[selectedMalfunctioningDay]).map((hour) => {
      return days[selectedMalfunctioningDay][hour]["malfunctions"].map(
        (malfunctionLog) => malfunctionLog.sensorId
      );
    });
  }, [days, selectedMalfunctioningDay]);

  //   const data = useMemo(() => {
  //     if (days && selectedMalfunctioningDay) {
  //       return {
  //         labels: Object.keys(days[selectedMalfunctioningDay]),
  //         datasets: ["north", "south", "east", "west"].map((face, index) => {
  //           return {
  //             label: face,
  //             data: Object.keys(days[selectedMalfunctioningDay]).map(
  //               (hour) =>
  //                 days[selectedMalfunctioningDay][hour]["malfunctions"].map(
  //                   (summary) => `${summary.sensorAvgTemperature}`
  //                 )
  //             ),
  //           };
  //         }),
  //       };
  //     }
  //     return { labels: [], datasets: [] };
  //   }, [days, selectedMalfunctioningDay]);

  const data = useMemo(() => {
    if (!days || !selectedMalfunctioningDay)
      return { labels: [], datasets: [] };

    const hours = Object.keys(days[selectedMalfunctioningDay]);

    // Map: sensorId => array of temps (aligned with `labels`)
    const sensorMap = {};

    for (const hour of hours) {
      const logs = days[selectedMalfunctioningDay][hour].malfunctions;

      for (const log of logs) {
        const { sensorId, sensorAvgTemperature } = log;

        if (!sensorMap[sensorId]) {
          // Init with nulls for previous hours
          sensorMap[sensorId] = Array(hours.indexOf(hour)).fill(null);
        }

        // Fill gap if needed
        const expectedLength = hours.indexOf(hour);
        while (sensorMap[sensorId].length < expectedLength) {
          sensorMap[sensorId].push(null); // missing data for this hour
        }

        sensorMap[sensorId].push(sensorAvgTemperature);
      }
    }

    // Normalize array lengths (for Chart.js)
    const maxLength = hours.length;
    Object.keys(sensorMap).forEach((sensorId) => {
      while (sensorMap[sensorId].length < maxLength) {
        sensorMap[sensorId].push(null);
      }
    });

    return {
      labels: hours,
      datasets: Object.entries(sensorMap).map(([sensorId, temps], index) => ({
        label: `Sensor ${sensorId}`,
        data: temps,
        borderColor: `hsl(${index * 60}, 70%, 50%)`,
        backgroundColor: `hsl(${index * 60}, 70%, 70%)`,
      })),
    };
  }, [days, selectedMalfunctioningDay]);

  return (
    <>
      <div className="flex flex-row flex-wrap w-[50vw] gap-x-[10px] gap-y-[10px] border-solid border-[1px] p-4">
        {Object.keys(days).map((day, dayIndex) => {
          return (
            <button
              className="bg-blue-10 text-white-10 p-2 rounded-md"
              key={dayIndex}
              onClick={() => setSelectedMalfunctioningSay(day)}
            >
              {day}
            </button>
          );
        })}
      </div>
      <div className="w-[50vw] h-[40vh] border-solid border-[1px]">
        <Line options={options} data={data} />
      </div>
    </>
  );
}

export default FaceMalfunctioningGraph;
