import { useEffect, useMemo, useState } from "react";

import { Chart, registerables } from "chart.js";
import { Bar, Line } from "react-chartjs-2";

Chart.register(...registerables);

function App() {
  const [weeklyReport, setWeeklyReport] = useState(null);
  const [selectedDay, setSelectedSay] = useState(null);

  const options = useMemo(() => {
    return {
      responsive: true,
      plugins: {
        legend: {
          position: "top",
        },
        title: {
          display: true,
          text: selectedDay ? selectedDay : "Chart",
        },
        scales: {
          y: {
            beginAtZero: true,
            title: {
              display: true,
              text: "Orders",
            },
          },
          x: {
            title: {
              display: true,
              text: "Date",
            },
          },
        },
      },
    };
  }, [selectedDay]);

  const getWeeklyReport = async () => {
    const response = await fetch("/api/get_weekly_summary_report", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await response.json();
    console.log(data);

    setWeeklyReport(data.weeklyReport);
    setSelectedSay(Object.entries(data.weeklyReport)[0][0]);
  };

  const data = useMemo(() => {
    if (weeklyReport && selectedDay) {
      return {
        labels: Object.keys(weeklyReport[selectedDay]),
        datasets: ["north", "south", "east", "west"].map((face, index) => {
          return {
            label: face,
            data: Object.keys(weeklyReport[selectedDay]).map(
              (hour) =>
                weeklyReport[selectedDay][hour]
                  .filter((summary) => summary.face === face)
                  .map((summary) => summary.avgTemperature)[0]
            ),
            backgroundColor: [
              "rgba(255, 99, 132, 0.5)",
              "rgba(53, 162, 235, 0.5)",
              "rgba(53, 62, 235, 0.5)",
              "rgba(53, 362, 25, 0.5)",
            ][index],
          };
        }),
      };
    }
    return { labels: [], datasets: [] };
  }, [weeklyReport, selectedDay]);

  useEffect(() => {
    if (!weeklyReport) {
      getWeeklyReport();
    }
  }, [weeklyReport]);

  return (
    <div className="bg-white-20 h-[100dvh] p-10 flex flex-col items-center">
      <h1 className="text-[25px]">Reporting</h1>
      {weeklyReport && (
        <div className="flex flex-col items-center p-4 gap-y-4 border-solid border-[1px]">
          <div>Last 7 Days</div>
          <div className="flex flex-col border-solid border-[1px] p-4 gap-y-4">
            <div className="flex flex-row gap-x-[10px] border-solid border-[1px] p-4">
              {Object.keys(weeklyReport).map((day, dayIndex) => {
                return (
                  <button
                    className="bg-blue-10 text-white-10 p-2 rounded-md"
                    key={dayIndex}
                  >
                    {day}
                  </button>
                );
              })}
            </div>
            <div className="w-full border-solid border-[1px]">
              <Line options={options} data={data} />
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
