import { useEffect, useMemo, useState } from "react";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  responsive: true,
  plugins: {
    legend: {
      position: "top",
    },
    title: {
      display: true,
      text: "Chart.js Bar Chart",
    },
  },
};

function App() {
  const [weeklyReport, setWeeklyReport] = useState(null);
  const [selectedDay, setSelectedSay] = useState(null);

  const getWeeklyReport = async () => {
    const response = await fetch("/api/get_weekly_summary_report", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await response.json();
    setWeeklyReport(data.weeklyReport);
    setSelectedSay(Object.entries(data.weeklyReport)[0][0]);
    console.log(data);
  };

  const labels = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
  ];

  const data = useMemo(() => {
    if (weeklyReport && selectedDay) {
      return {
        labels,
        datasets: [
          {
            label: "Dataset 1",
            data: weeklyReport[selectedDay].map((hour) =>
              hour.filter((summary) => summary.face === "north")
            ),
            backgroundColor: "rgba(255, 99, 132, 0.5)",
          },
        ],
      };
    }
    return {};
  }, [weeklyReport, selectedDay]);

  useEffect(() => {
    if (!weeklyReport) {
      getWeeklyReport();
    }
  }, [weeklyReport]);

  return (
    <div className="bg-white-20 h-[100dvh] p-10 flex flex-col items-center">
      <h1 className="text-[25px]">Reporting</h1>
      <div>
        <div>Last 7 Days</div>
        <div className="w-[50vw] border-solid border-[1px]">
          <Bar options={options} data={data} />;
        </div>
      </div>
    </div>
  );
}

export default App;
